import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);



export default function ChartCard({ title }) {
  const data = {
    labels: ["Mon", "Tue", "Wed", "Thu", "Fri"],
    datasets: [
      {
        label: "Attention Level %",
        data: [75, 68, 82, 70, 78],
        backgroundColor: "#D4AF37",
        borderRadius: 8,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        labels: {
          color: "#EAEAEA",
        },
      },
    },
    scales: {
      x: {
        ticks: { color: "#9A9A9A" },
        grid: { display: false },
      },
      y: {
        ticks: { color: "#9A9A9A" },
        grid: { color: "#222" },
      },
    },
  };

  return (
    <div className="card chart-card">
      <h4>{title}</h4>
      <Bar data={data} options={options} />
    </div>
  );
}
