import React, { useState } from 'react';

function App() {
  // تخزين اللون الحالي في state
  const [backgroundColor, setBackgroundColor] = useState('#FFFFFF');

  // وظيفة لتوليد لون عشوائي
  const generateRandomColor = () => {
    const randomColor = `#${Math.floor(Math.random() * 16777215).toString(16).padStart(6, '0')}`;
    return randomColor;
  };

  // وظيفة لتغيير لون الخلفية
  const changeBackgroundColor = () => {
    const newColor = generateRandomColor();
    setBackgroundColor(newColor); // تحديث اللون الحالي
  };

  return (
    <div style={{ backgroundColor, height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
      <button 
        onClick={changeBackgroundColor} 
        style={{
          padding: '10px 20px', 
          fontSize: '16px', 
          borderRadius: '5px', 
          border: 'none', 
          cursor: 'pointer',
          backgroundColor: '#000',
          color: '#FFF'
        }}
      >
        غيّر لون الخلفية
      </button>
    </div>
  );
}

export default App;
