import React, { useState } from "react";
import "./BookLogin.css";

const BookLogin = () => {
  const [flipped, setFlipped] = useState(false); // false = Login, true = SignUp

  const handleFlip = () => setFlipped(!flipped);

  return (
    <div className="book-container">
      <div className={`book ${flipped ? "flipped" : ""}`}>
        
        {/* صفحة Login */}
        <div className="page page-front">
          <h2>Login</h2>
          <input type="email" placeholder="Email" />
          <input type="password" placeholder="Password" />
          <button>Sign In</button>
          <p>
            Don't have an account?{" "}
            <span onClick={handleFlip}>Sign Up</span>
          </p>
        </div>

        {/* صفحة Sign Up */}
        <div className="page page-back">
          <h2>Sign Up</h2>
          <input type="text" placeholder="Full Name" />
          <input type="email" placeholder="Email" />
          <input type="password" placeholder="Password" />
          <button>Create Account</button>
          <p>
            Already have an account?{" "}
            <span onClick={handleFlip}>Login</span>
          </p>
        </div>

      </div>
    </div>
  );
};

export default BookLogin;
