import { useState } from "react";
import "../styles/Auth.css";

const Auth = () => {  const [flipped, setFlipped] = useState(false); // false = الغلاف مغلق

  return (
    <div className="auth-container">
      <div className={`book ${isSignup ? "flipped" : ""}`}>
        
        {/* الغلاف الأمامي */}
       <div className="cover">
  <h2>FocusMate</h2>

  {!flipped && (
    <button
      className="open-book-btn"
      onClick={() => setFlipped(true)}
    >
      Open Book
    </button>
  )}
</div>

        {/* الصفحة الداخلية Login */}
        <div className="page">
          <h2>Login</h2>
          <input type="email" placeholder="Email" />
          <input type="password" placeholder="Password" />
          <button>Login</button>
          <p onClick={() => setIsSignup(true)}>Create an account</p>
        </div>

        {/* الصفحة الداخلية Sign Up */}
        <div className="page back">
          <h2>Sign Up</h2>
          <input type="text" placeholder="Full Name" />
          <input type="email" placeholder="Email" />
          <input type="password" placeholder="Password" />
          <button>Create Account</button>
          <p onClick={() => setIsSignup(false)}>Already have an account?</p>
        </div>

      </div>
    </div>
  );
};

export default Auth;
