import React, { useState } from 'react';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]); // قائمة المهام
  const [task, setTask] = useState('');  // المهمة الحالية

  // إضافة مهمة جديدة
  const addTask = () => {
    if (task.trim()) {
      setTasks([...tasks, task]);
      setTask('');
    }
  };

  // حذف مهمة
  const deleteTask = (index) => {
    const newTasks = tasks.filter((_, i) => i !== index);
    setTasks(newTasks);
  };

  return (
    <div className="App">
      <h1>قائمة المهام</h1>
      <div>
        <input
          type="text"
          placeholder="أضف مهمة جديدة"
          value={task}
          onChange={(e) => setTask(e.target.value)}
        />
        <button onClick={addTask}>إضافة</button>
      </div>
      <ul>
        {tasks.map((t, index) => (
          <li key={index}>
            {t}
            <button onClick={() => deleteTask(index)}>حذف</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
