import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);



export default function ChartCard({ title }) {
 
const data = {
  labels: ["Week 1", "Week 2", "Week 3", "Week 4"],
  datasets: [
    {
      label: "Average Attention",
      data: [72, 75, 70, 78],
      borderColor: "#F1D27A",
      backgroundColor: "rgba(212,175,55,0.2)",
      tension: 0.4,
    },
  ],
};

}
