import React, { useState } from 'react';
import './App.css';

function App() {
  const [BackgroundColor,setBackgroundColor]=useState("#FFFFFF")
 const generateRandomColor=()=>{
    const randomcolor=`#${Math.floor(Math.random() * 16777215).toString(16).padStart(6, '0')}`;
    return randomcolor;
  }
  const changeBackgroundColor=()=>{
  const newcolor=generateRandomColor()
  setBackgroundColor(newcolor)
  }
  return (
    <div className="App"style={{ backgroundColor, height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
      <button   style={{
        padding: '10px 20px', 
        fontSize: '16px', 
        borderRadius: '5px', 
        border: 'none', 
        cursor: 'pointer',backgroundColor: '#000',
          color: '#FFF'}}  onClick={changeBackgroundColor}>غير لون الخلفية</button>
    </div>
  );
}

export default App;
