import React, { useState } from 'react';
import './App.css';

function App() { 
  const [tasks,settasks]=useState([])
  const [task,settask]=useState(' ')

    const addtask= () => {
      if(task.trim()){
        settasks([...tasks,task])
        settask(' ')
   }
  }
    const delet=(index)=>{
      const newtasks=tasks.filter((_,i)=>i !==index) 
      settasks(newtasks)
    }
  return(
    <div className='App' > 
   <div>
   <input  placeholder='اضف مهمة' value={task} onChange={(e)=>settask(e.target.value)}></input>
    <button className="button" onClick={addtask}>enter</button>
    </div>
    <div>
      <ul>
       {tasks.map((t,index)=>(
       <li>
        {t}
        <button className="button" onClick={()=>delet(index)}>delet</button>
       </li>     
      ))} 
      </ul>
    </div>
   </div>
  )
}
export default App;
